import React, { Component } from 'react';
import ReactEcharts from 'echarts-for-react';

// alphavantage API: 4ZHSS420ZVOBWM4I

var func = 'FX_DAILY';
var from_symbol = 'USD';
var to_symbol = 'JPY';
const apikey = '4ZHSS420ZVOBWM4I';
var URL = `https://www.alphavantage.co/query?function=${func}&from_symbol=${from_symbol}&to_symbol=${to_symbol}&apikey=${apikey}`;

class App extends Component {
  constructor() {
    super();
    this.state = {
      values: []
    };
  }

  componentWillMount() {
    fetch(URL)
    .then(results => {
      return results.json();
    }).then(data => {
      let values = data.results.map((val) => {
        return (
          <div>
            {val}
          </div>
        );
      });
      this.setState({values: values});
      console.log('state', this.state.values);
    })
  }

  render() {
    console.log(URL);
    return (
      <div>
        {this.state.values}
      </div>
    )
  }
}

export default App;
