import React, { Component } from 'react';
import ReactEcharts from 'echarts-for-react';

// alphavantage API: 4ZHSS420ZVOBWM4I

var func = 'FX_DAILY';
var from_symbol = 'USD';
var to_symbol = 'JPY';
const apikey = '4ZHSS420ZVOBWM4I';
var URL = `https://www.alphavantage.co/query?function=${func}&from_symbol=${from_symbol}&to_symbol=${to_symbol}&apikey=${apikey}`;
var date_array = [];
var close_values = [];

class App extends Component {
  constructor() {
    super();
    this.state = {
      values: []
    };
  }

  componentDidMount() {
    fetch(URL)
    .then(results => {
      return results.json();
    }).then(data => {
      let values = data['Time Series FX (Daily)'];
      for (var date in values) {
        date_array.push(date);
        close_values.push(parseFloat(values[date]["3. low"]));
      }
      this.setState({values: [12,12, 21,3, 21, 21, 12]});
    })
  }

  render() {
    console.log(date_array);
    console.log(close_values);
    console.log(['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'])
    return (
      <ReactEcharts
        option={{
          xAxis: {
            type: 'category',
            data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
          },
          yAxis: {
            type: 'value'
          },
          series: [{
            type: 'line',
            data: this.state.values
          }]
        }}
      />
    );
  }
}

export default App;
